
public class AlphaCheck {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char alpha='a';
		if(alpha>='a' && alpha<='z')
		System.out.println("Entered alphabet is small case.");
		else if(alpha>='A' && alpha<='Z')
		System.out.println("Entered alphabet is Capital case.");
		else
		System.out.println("Entered character is not an alphabet.");

	}

}
